for link in links:
    driver.get(link)
    time.sleep(2)
# print all text in driver
    for title in driver.find_element(By.TAG_NAME, 'div').find_elements(By.TAG_NAME, 'a'):
        try:
            # print href from a tag
            theme = title.text
            if link.startswith(title.get_attribute('href')) and len(theme) > 2:
                theme = str(title.text)
                print(f'TITLE: {title.text}')
                # index = title.find('from ')
                # theme = title[index+5:]
                video = driver.find_element(By.TAG_NAME, 'video')
                src = video.get_attribute('src')
                # print(theme, src)
                if theme not in themes:
                    themes[theme] = []
                    themes[theme].append(src)
                    print(theme)
                else:
                    themes[theme].append(src)

                break
        except:
            print('error')
            pass

driver.close()
driver.quit()
print(themes)

